INSERT INTO `VARIABLE_NAMES` VALUES (43,'pcr primers','PCR primers that were used to amplify the sequence of the targeted gene, locus or subfragment. This field should contain all the primers used for a single PCR reaction if multiple forward or reverse primers are present in a single PCR reaction. The primer sequence should be reported in uppercase letters','FWD:{dna} REV:{dna}','pcr_primers','GSC_v4.0','experiment','n','n','all',NULL);
INSERT INTO `VARIABLE_NAMES` VALUES (44,'multiplex identifiers','Molecular barcodes, called Multiplex Identifiers (MIDs), that are used to specifically tag unique samples in a sequencing run. Sequence should be reported in uppercase letters','{dna}','mid','GSC_v4.0','experiment','n','n','all',NULL);
INSERT INTO `VARIABLE_NAMES` VALUES (45,'adapters','Adapters provide priming sequences for both amplification and sequencing of the sample-library fragments. Both adapters should be reported; in uppercase letters','{dna},{dna}','adapters','GSC_v4.0','experiment','n','n','all',NULL);
INSERT INTO `VARIABLE_NAMES` VALUES (46,'pcr conditions','Description of reaction conditions and components for PCR in the form of  \'initial denaturation:94degC_1.5min; annealing=...\'','initial denaturation:degrees_minutes; annealing:degrees_minutes; elongation: degrees_minutes; final elongation:degrees_minutes; total cycles','pcr_cond','GSC_v4.0','experiment','n','n','all',NULL);
