INSERT INTO `VARIABLE_NAMES` VALUES (48,'sequence quality check','Indicate if the sequence has been called by automatic systems (none) or undergone a manual editing procedure (e.g. by inspecting the raw data or chromatograms). Applied only for sequences that are not submitted to SRA or DRA','[none|manually edited]','seq_quality_check','GSC_v4.0','analysis','n','n','all',NULL);
INSERT INTO `VARIABLE_NAMES` VALUES (49,'chimera check','A chimeric sequence, or chimera for short, is a sequence comprised of two or more phylogenetically distinct parent sequences. Chimeras are usually PCR artifacts thought to occur when a prematurely terminated amplicon reanneals to a foreign DNA strand and is copied to completion in the following PCR cycles. The point at which the chimeric sequence changes from one parent to the next is called the breakpoint or conversion point','{text} {text}','chimera_check','GSC_v4.0','analysis','n','n','all',NULL);
INSERT INTO `VARIABLE_NAMES` VALUES (50,'assembly','How was the assembly done (e.g. with a text based assembler like phrap or a flowgram assembler); estimated error rate associated with the finished sequences (e.g. error rate of 1 in 1000 bp); and the method of calculation. E.g.{assembly method}; {estimated error rate}; {method of calculation}','{text};{text};{text}','assembly','GSC_v2.1','analysis','n','n','all',NULL);
